PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `package_buffs` (
	`Name`	TEXT,
	`Level`	INTEGER,
	`Information`	TEXT,
	PRIMARY KEY(`Name`,`Level`)
);
CREATE TABLE "packages" (
	`Name`	TEXT NOT NULL UNIQUE,
	`Type`	TEXT NOT NULL,
	`Description`	TEXT,
	`Motivation`	TEXT,
	`Roll`	TEXT,
	PRIMARY KEY(`Name`)
);
COMMIT;
